    val especialCharRegex = ".*[~!@#\$%\\^&*()\\-_=+\\|\\[{\\]};:'\",<.>/?].*"
    var passwordPatternsWithEspecialChar = Pattern.compile(especialCharRegex)
    var passwordPaternWithOneNumber = Pattern.compile(oneNumberRegex, Pattern.CASE_INSENSITIVE)

    public fun validarSenha (pass1:String):Boolean {
        if (pass1.length < 8){
            return false
        } else if (!(this.passwordPaternWithOneNumber.matcher(pass1).matches())){
            return false
        } else if (!(this.passwordPatternsWithEspecialChar.matcher(pass1).matches())) {
            return false
        }

        return true
    }

    public fun validarEmail (email:String, context: Context):Boolean {
        val ptr = Pattern.compile(this.emailRegex)

        return (ptr.matcher(email).matches())
    }

    public fun validar2Senhas (pass1:String, pass2:String):Boolean {
        if (pass1.equals(pass2)){
            return true
        }
        return false
    }

    private fun showToast(context: Context, content: String) {
        Toast.makeText(
            context,
            content,
            Toast.LENGTH_SHORT
        ).show()
    }
}